import { React, useState } from 'react';
import './CreateMate.css'
import { supabase } from '../client';

const CreateMate = () => {

    const [mate, setMate] = useState({ name: "", speed: 0}) // do color later

    const handleChange = (event) => {
        const { name, value } = event.target;
        setmate((prev) => {
            return {
                ...prev,
                [name]: value,
            }
        })
    }
    const createMate = async (event) => {
        event.preventDefault();
        await supabase
            .from('crewmate')
            .insert({ name: mate.name, speed: mate.speed})
            .select();
        window.location = '/';

    }
    return (
        <div>
            <form>
                <label for="name">Name</label> <br />
                <input type="text" id="name" name="name" onChange={handleChange} /><br />
                <br />

                <label for="speed">Speed</label><br />
                <input type="text" id="speed" name="speed" onChange={handleChange} /><br />
                <br />

                <input type="submit" value="Submit" onClick={createMate} />
            </form>
        </div>
    )
}

export default CreateMate
