import React from "react";
import { useParams } from "react-router-dom";

const MateDetail = ({ mates }) => {
    const { id } = useParams();

    // Check if mates array is empty or undefined
    if (!mates || mates.length === 0) {
        return <div>No mate data available</div>;
    }

    const mate = mates.find(m => m.id === id);

    if (!mate) {
        return <div>Mate not found</div>;
    }

    return (
        <div>
            <h2>{mate.name}</h2>
            <p>Speed: {mate.speed}</p>
            <p>Color: {mate.color}</p>
            {/* Add additional details as needed */}
        </div>
    );
};

export default MateDetail;
