import React from 'react'
import { useState } from 'react'
import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'
import {supabase} from '../client'

const Card = (props) =>  {

  const [count, setCount] = useState(0)

  const updateCount = async (event) => {
    event.preventDefault();

    await supabase
      .from('from')
      // .update({ betCount: count + 1})
      .eq('id', props.id)

    setCount((count) => count + 1);
  }

  return (
      <div className="Card" style={{ backgroundColor: 'red'}}>
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          <Link to={'detail/'+ props.id} style={{ textDecoration: 'none', color: 'inherit' }}>
          <h2 className="title">{props.name}</h2>
          <h3 className="descriptio">{props.speed +" mph"}</h3>
          <p className="color">{props.color}</p>
          </Link>
          {/* <button className="betButton" onClick={updateCount} >üëç Bet Count: {count}</button> */}

      </div>
  );
};

export default Card;
