import { React, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './EditPost.css'
import { supabase } from '../client';

const EditPost = ({ data }) => {

    const { id } = useParams();
    const [mate, setMate] = useState({ id: null, name: "", author: "", description: "" });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost((prev) => {
            return {
                ...prev,
                [name]: value,
            }
        })
    }
    const updatePost = async (event) => {
        event.preventDefault();

        await supabase
            .from('crewmate')
            .update({ name: mate.name, speed: mate.speed, color: mate.color })
            .eq('id', id);

        window.location = "/";
    }

    const deletePost = async (event) => {
        event.preventDefault();
        await supabase
            .from('crewmate')
            .delete()
            .eq('id', id);
        window.location = '/';
    }
    return (
        <div>
            <form>
                <label htmlFor="name">Name</label> <br />
                <input type="text" id="name" name="name" onChange={handleChange} /><br />
                <br />

                <label htmlFor="speed">Speed</label><br />
                <input type="text" id="speed" name="speed" onChange={handleChange} /><br />
                <br />

                <label htmlFor="color">Color</label><br />
                <input type="text" id="color" name="color" onChange={handleChange} /><br />
                <br />

                <input type="submit" value="Submit" onClick={updatePost} />
                <button className="deleteButton" onClick={deletePost}>Delete</button>
            </form>
        </div>
    )
}

export default EditPost
