import React, { useState, useEffect } from 'react';
import './CreateMate.css';
import { supabase } from '../client';

const CreateMate = () => {
    const [mate, setMate] = useState({ name: "", speed: 0, color: "#ffffff" });

    useEffect(() => {
        // Convert UUID to hexadecimal color
        const hexColor = uuidToHex(mate.color);
        setMate(prev => ({
            ...prev,
            color: hexColor
        }));
    }, [mate.color]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setMate((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const uuidToHex = (uuid) => {
        const hex = [];
        for (let i = 0; i < 16; i++) {
            hex.push(uuid[i]);
        }
        return "#" + hex.join('');
    };

    const createMate = async (event) => {
        event.preventDefault();
        try {
            const { data, error } = await supabase
                .from('crewmate')
                .insert({ name: mate.name, speed: mate.speed, color: mate.color })
                .single();

            if (error) {
                console.error('Error creating crewmate:', error.message);
                return;
            }

            console.log('Crewmate created successfully:', data);
            window.location = '/';
        } catch (error) {
            console.error('Error creating crewmate:', error.message);
        }
    };

    return (
        <div>
            <form>
                <label htmlFor="name">Name</label> <br />
                <input type="text" id="name" name="name" onChange={handleChange} /><br />
                <br />

                <label htmlFor="speed">Speed</label><br />
                <input type="text" id="speed" name="speed" onChange={handleChange} /><br />
                <br />

                <label htmlFor="color">Color</label><br />
                <input type="color" id="color" name="color" value={mate.color} onChange={handleChange} /><br />
                <br />

                <input type="submit" value="Submit" onClick={createMate} />
            </form>
        </div>
    );
};

export default CreateMate;
