import React, { useState } from 'react';
import './CreateMate.css';
import { supabase } from '../client';

const CreateMate = () => {
    const [mate, setMate] = useState({ title: "", description: "", url: "", created_at:null}); //props title, description, image 

    const formatDateDifference = (createdAt) => {
    const currentDate = new Date();
    const createdDate = new Date(createdAt);
    const timeDifference = currentDate - createdDate;

    const minuteDifference = Math.floor(timeDifference / 60000); // 1 minute = 60000 milliseconds
    const hourDifference = Math.floor(minuteDifference / 60);
    const dayDifference = Math.floor(hourDifference / 24);
    const monthDifference = Math.floor(dayDifference / 30);
    const yearDifference = Math.floor(monthDifference / 12);

    if (yearDifference > 0) {
        return `${yearDifference} year${yearDifference !== 1 ? 's' : ''} ago`;
    } else if (monthDifference > 0) {
        return `${monthDifference} month${monthDifference !== 1 ? 's' : ''} ago`;
    } else if (dayDifference > 0) {
        return `${dayDifference} day${dayDifference !== 1 ? 's' : ''} ago`;
    } else if (hourDifference > 0) {
        return `${hourDifference} hour${hourDifference !== 1 ? 's' : ''} ago`;
    } else if (minuteDifference > 0) {
        return `${minuteDifference} minute${minuteDifference !== 1 ? 's' : ''} ago`;
    } else {
        return 'just now';
    }
};
const handleChange = (event) => {
        const { name, value } = event.target;
        setMate((prev) => ({
            ...prev,
            [name]: value,
            created_at: new Date().toISOString()
        }));
    };

    const createMate = async (event) => {
        event.preventDefault();
        try {
            const { data, error } = await supabase
                .from('info')
                .insert({ title: mate.title, description: mate.description , url: mate.url,
                    created_at: mate.created_at})
                .single();

            if (error) {
                console.error('Error creating crewmate:', error.message);
                return;
            }

            console.log('Crewmate created successfully:', data);
            window.location = '/gallery';
        } catch (error) {
            console.error('Error creating crewmate:', error.message);
        }
    };


    return (
        <div>
            <form>
                <label htmlFor="title">Title</label> <br />
                <input type="text" id="title" name="title" onChange={handleChange} /><br />
                <br />

                <label htmlFor="description">Description</label><br />
                <input type="text" id="description" name="description" onChange={handleChange} /><br />
                <br />

                <label htmlFor="url">Image URL</label><br />
                <input type="text" id="url" name="url" onChange={handleChange} /><br />
                <br />

                <input type="submit" value="Submit" onClick={createMate} />
            </form>
        </div>
    );
};

export default CreateMate;
