import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom'
import ReadPosts from './pages/ReadPosts'
import CreateMate from './pages/CreateMate'
import EditPost from './pages/EditPost'
import { Link } from 'react-router-dom'


const App = () => {


  const mates = [
      {'id':'1', 
      'name': 'oliber',
      'speed': 5, 
      'color': 'pink'},
  ]


  // Sets up routes
  let element = useRoutes([
    {
      path: "/",
      element:<ReadPosts data={mates}/>
    },
    {
      path:"/edit/:id",
      element: <EditPost data={mates} />
    },
    {
      path:"/new",
      element: <CreateMate />
    },
    {
      path:"/detail/:id",
      element: <CreateMate />
    }
  ]);

  return ( 

    <div className="App">

      <div className="header">
        <h1>üëç Bet 1.0</h1>
        <Link to="/"><button className="headerBtn"> Explore Challenges üîç  </button></Link>
        <Link to="/new"><button className="headerBtn"> Submit Challenge üèÜ </button></Link>
      </div>
        {element}
    </div>

  );
}

export default App;
