import React, { useState } from 'react';
import './CreateMate.css';
import { supabase } from '../client';

const CreateMate = () => {
    const [mate, setMate] = useState({ title: "", descrip: "", url: "white"}); //props title, description, image 

    const handleChange = (event) => {
        const { name, value } = event.target;
        setMate((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const createMate = async (event) => {
        event.preventDefault();
        try {
            const { data, error } = await supabase
                .from('info')
                .insert({ name: mate.name, descrip: mate.descrip , url: mate.url})
                .single();

            if (error) {
                console.error('Error creating crewmate:', error.message);
                return;
            }

            console.log('Crewmate created successfully:', data);
            window.location = '/';
        } catch (error) {
            console.error('Error creating crewmate:', error.message);
        }
    };


    return (
        <div>
            <form>
                <label htmlFor="title">Title</label> <br />
                <input type="text" id="title" name="title" onChange={handleChange} /><br />
                <br />

                <label htmlFor="descrip">Description</label><br />
                <input type="text" id="descrip" name="descrip" onChange={handleChange} /><br />
                <br />

                <label htmlFor="url">Image URL</label><br />
                <input type="text" id="url" name="url" onChange={handleChange} /><br />
                <br />

                <input type="submit" value="Submit" onClick={createMate} />
            </form>
        </div>
    );
};

export default CreateMate;
