import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom'
import ReadPosts from './pages/ReadPosts'
import CreateMate from './pages/CreateMate'
import EditPost from './pages/EditPost'
import MateDetail from './pages/MateDetail'

import { Link } from 'react-router-dom'


const App = () => {


  const mates = [
      {'id':'1', 
      'name': 'oliber',
      'speed': 5, 
      'color': 'pink'},
  ]


  // Sets up routes
  let element = useRoutes([
    {
      path: "/",
      element:<ReadPosts data={mates}/>
    },
    {
      path:"/edit/:id",
      element: <EditPost data={mates} />
    },
    {
      path:"/new",
      element: <CreateMate />
    },
    {
      path:"/detail/:id",
      element: <MateDetail />
    }
  ]);

  return ( 

    <div className="App">

      <div className="header">
        <h1>Crewmates</h1>
        <Link to="/home"><button className="headerBtn"> Crewmate Gallery  </button></Link>
        <Link to="/new"><button className="headerBtn"> Create Crewmate </button></Link>
      </div>
        {element}
    </div>

  );
}

export default App;
